// import logo from './logo.svg';
import './App.css';
import React from 'react';
import Button from './button';
import Eval from './eval';


function App() {

  const [a,setA]=React.useState("")
  const [s,setS]=React.useState(false)

  const style={
    display: s ? "block" : "none"
  }

  function value(c){
    var k=a
    if(a===Infinity){k=""; console.log(k);}
    if(a==="syntax error"){k=""; console.log(k);}
    console.log();
  console.log(a+c)
  setA(k+c)

   }

   function calc(){
    try{
      setA(String(parseFloat(eval(a).toFixed(3))))
    }
    catch(exception){setA("syntax error")}

     }

function clearit(){
  setA("")

}
function backspace(){
  // console.log(a+a) to check the value is in string or not
 String(a)
  setA(()=>{return a.slice(0, -1)})
}

function onoff(){
  setA("")
  if(s===false){setS(true)}
  else{setS(false)}
}


  return (
    <div>

       <div id="main">
       <center> <div id="display">
       <p style={style} >{a}</p>
       </div>
        </center>
        <div align="center">
        <div className="flux-container" style={{justifyContent: "right"}}>
             {/* <button>clear</button>  */}
        </div>
        <div className="flux-container">
        <button  style={{justifyContent: ""}} onClick={onoff}>on/off</button>
        <button style={{justifyContent: ""}} onClick={clearit}>clear</button>
        <button  style={{justifyContent: ""}} onClick={backspace}>delete</button>

      </div>
        <div className="flux-container">
            <Button c={"9"} value={value} />
            <Button c={"8"} value={value} />
            <Button c={"7"} value={value} />
            <Button c={"/"} value={value} />
        </div>
        <div className="flux-container">
        <Button c={"6"} value={value} />
            <Button c={"5"} value={value} />
            <Button c={"4"} value={value} />
            <Button c={"*"} value={value} />
        </div>
        <div className="flux-container">
            <Button c={"3"} value={value} />
            <Button c={"2"} value={value} />
            <Button c={"1"} value={value} />
            <Button c={"+"} value={value} />
        </div>
            <div className="flux-container">
            <button style={{fontSize:"25px"}} onClick={()=>{let c=".";value(c);}}>.</button>
                <Button c={"0"} value={value} />
                <Eval c={"="} calc={calc} />
                <Button c={"-"} value={value} />
            </div>
        </div>   

    </div>

    </div>
  );
}

export default App;
