// import logo from './logo.svg';
import './App.css';
import React from 'react';
import Button from './button';
import Eval from './eval';
function App() {

  const [a,setA]=React.useState("")

  function value(c){
  var x=c
  console.log(a+x)
  setA(a+x)
   }

   function calc(){
    setA(eval(a))
     }

  return (
    <div>

       <p>{a}</p>

       <div id="main">
       <center> <div id="display">
        <p id="screen"></p>
       </div>
        </center>
        <div align="center">
        <div class="flux-container" style="justify-content: right;">
             {/* <button>clear</button>  */}
        </div>
        <div class="flux-container" style="justify-content: right;">
          <button   onclick="power();">on/off</button>
      </div>
        <div class="flux-container">
            <Button c={"9"} value={value} />
            <Button c={"8"} value={value} />
            <Button c={"7"} value={value} />
            <Button c={"/"} value={value} />
        </div>
        <div class="flux-container">
        <Button c={"6"} value={value} />
            <Button c={"5"} value={value} />
            <Button c={"4"} value={value} />
            <Button c={"*"} value={value} />
        </div>
        <div class="flux-container">
            <Button c={"3"} value={value} />
            <Button c={"2"} value={value} />
            <Button c={"1"} value={value} />
            <Button c={"+"} value={value} />
        </div>
            <div class="flux-container">
            <button className='clear' onClick={clear()}>clear</button>
                <Button c={"0"} value={value} />
                <Eval c={"="} calc={calc} />
                <Button c={"-"} value={value} />
            </div>
        </div>   

    </div>

    </div>
  );
}

export default App;
