// import logo from './logo.svg';
import './App.css';
import React from 'react';
import Button from './button';
import Eval from './eval';


function App() {

  // const off={
  //   display:"none"
  // }
  // const on={
  //   display:"block"
  // }


  const [a,setA]=React.useState("")
  const [s,setS]=React.useState(false)
  // const [display,setDisplay]=React.useState(off)
  const [e,setE]=React.useState("")

  const style={
    display: s ? "block" : "none"
  }

  function value(c){
  setE("")
  var x=c
  console.log(a+x)
  setA(a+x)

   }

   function calc(){
    try{
      setA(eval(a).toFixed(4))
    }
    catch(exception){setE("syntax error");setA("")}

     }

function clearit(){
  setA("")
}
function backspace(){
  setA(a.slice(0, -1))
}

// function onoff(){
//   if(s===false){setDisplay(on); setS(true)}
//   else{setDisplay(off); setS(false)}
// }

function onoff(){
  setA("")
  if(s===false){setS(true)}
  else{setS(false)}
}


  return (
    <div>

       <div id="main">
       <center> <div id="display">{e}
       <p style={style} >{a}</p>
       </div>
        </center>
        <div align="center">
        <div className="flux-container" style={{justifyContent: "right"}}>
             {/* <button>clear</button>  */}
        </div>
        <div className="flux-container">
        <button  style={{justifyContent: "left"}} onClick={onoff}>on/off</button>
        <button style={{justifyContent: "center"}} onClick={clearit}>clear</button>
        <button  style={{justifyContent: "right"}} onClick={backspace}>delete</button>
      </div>
        <div className="flux-container">
            <Button c={"9"} value={value} />
            <Button c={"8"} value={value} />
            <Button c={"7"} value={value} />
            <Button c={"/"} value={value} />
        </div>
        <div className="flux-container">
        <Button c={"6"} value={value} />
            <Button c={"5"} value={value} />
            <Button c={"4"} value={value} />
            <Button c={"*"} value={value} />
        </div>
        <div className="flux-container">
            <Button c={"3"} value={value} />
            <Button c={"2"} value={value} />
            <Button c={"1"} value={value} />
            <Button c={"+"} value={value} />
        </div>
            <div className="flux-container">
            <button  onClick={backspace}>delete</button>
                <Button c={"0"} value={value} />
                <Eval c={"="} calc={calc} />
                <Button c={"-"} value={value} />
            </div>
        </div>   

    </div>

    </div>
  );
}

export default App;
