// import logo from './logo.svg';
import './App.css';
import React from 'react';
import Button from './button';
import Eval from './eval';
function App() {

  const [a,setA]=React.useState("")
  const [s,setS]=React.useState(false)
  function value(c){
  var x=c
  console.log(a+x)
  setA(a+x)
   }

   function calc(){
    setA(eval(a))
     }

function clearit(){
  setA("")
}

function onoff(){
if(s===false){setS(true)}
else{setS(false)}
}

  return (
    <div>

       <p>{a}</p>

       <div id="main">
       <center> <div id="display">{a}</div>
        </center>
        <div align="center">
        <div className="flux-container" style={{justifyContent: "right"}}>
             {/* <button>clear</button>  */}
        </div>
        <div className="flux-container" style={{justifyContent: "right"}}>
        <button  onClick={onoff}>on/off</button>
      </div>
        <div className="flux-container">
            <Button c={"9"} value={value} />
            <Button c={"8"} value={value} />
            <Button c={"7"} value={value} />
            <Button c={"/"} value={value} />
        </div>
        <div className="flux-container">
        <Button c={"6"} value={value} />
            <Button c={"5"} value={value} />
            <Button c={"4"} value={value} />
            <Button c={"*"} value={value} />
        </div>
        <div className="flux-container">
            <Button c={"3"} value={value} />
            <Button c={"2"} value={value} />
            <Button c={"1"} value={value} />
            <Button c={"+"} value={value} />
        </div>
            <div className="flux-container">
            <button  onClick={clearit}>clear</button>
                <Button c={"0"} value={value} />
                <Eval c={"="} calc={calc} />
                <Button c={"-"} value={value} />
            </div>
        </div>   

    </div>

    </div>
  );
}

export default App;
